module.directive("ShowErrors", function (g) {
    return {
        restrict: "A", require: "^form", link: function (c, b, a, d) {
            a = b[0].querySelector("[name]");
            a = angular.element(a);
            var e = a.attr("name"), f = !1;
            a.bind("blur", function () {
                f = !0;
                b.toggleClass("has-error", d[e].$invalid)
            });
            c.$watch(function () {
                return d[e].$invalid
            }, function (a) {
                !f && a || b.toggleClass("has-error", a)
            });
            c.$on("show-errors-check-validity", function () {
                b.toggleClass("has-error", d[e].$invalid)
            });
            c.$on("show-errors-reset", function () {
                g(function () {
                        b.removeClass("has-error")
                    },
                    0, !1)
            })
        }
    }
});
